INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (2, '«Q» UNIVERSITY', 'Almaty', '«Q» University» является правопреемником Академии «Qainar» – первого частного высшего учебного заведения в независимом Казахстане. За 30 лет была реализована идея непрерывности обучения от средней школы «Бакалавриат» и Колледжа «Кайнар» до многопрофильного высшего учебного заведения. С 2010 года  «Q» University» является участником Болонского процесса, предусматривающего вхождение в мировое образовательное сообщество и признание легитимности дипломов наших выпускников в зарубежных странах.  В структуре «Q» University» - 5 академических школ, которые осуществляют подготовку кадров по 8 направлениям. Наиболее востребованные образовательные программы «Q» University» – право, психология, журналистика, финансы, учет и аудит, информационные технологии и транспортные услуги. Университет осуществляет международное сотрудничество по подготовке кадров, совместным научным исследованиям, проведению международных научных мероприятий. Имеет официальные соглашения по долговременному двухстороннему сотрудничеству с 52 вузами и научно-исследовательскими организациями США, Италии, Германии, Кореи, Китая, Индии и др.  В распоряжении студентов - общежитие на более чем 150 мест, богатый библиотечный фонд, крытый спортзал, спортивные секции по боевым искусствам, опытный профессорско-преподавательский состав, высокотехнологичные аудитории, Центр карьеры. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (3, 'ALMATY MANAGEMENT UNIVERSITY', 'Almaty', 'В AlmaU функционируют 9 Школ, 4 центра: Институт Развития Образования и AlmaU Extension: Высшая Школа Бизнеса, Школа Менеджмента, Школа Экономики и Финансов, Школа Политики и Права, Школа Цифровых Технологий, Школа Предпринимательства и Инноваций, Школа Гостеприимства и Туризма, Школа Медиа и Кино, AlmaU Sharmanov School of Health Sciences, Центр Урбанистики, Центр Креативных Индустрий, Языковой Центр и Центр Спортивного Менеджмента.  Обучение проходит на 3-х языках: казахском, русском и английском, по более чем 20 специальностям, среди которых: менеджмент, финансы, маркетинг, ресторанное дело и гостиничный бизнес, международные отношения и экономика, software engineering, data science и др.  Университет является партнером Arizona State University (ASU), крупнейшего государственного университета США. Вузы совместно реализуют  образовательную программу: Global Management, по окончании которой выпускники получают степень бакалавра International Trade в Университете штата Аризона в дополнение к диплому AlmaU.  В распоряжении студентов:  Университет сотрудничает с общежитием @house_of_students_, которое выделяет для студентов AlmaU от 100 мест: ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (4, 'CASPIAN UNIVERSITY', 'Almaty', 'Caspian University является многопрофильным вузом и позиционируется на рынке образовательных услуг как университет, обеспечивающий качественное, непрерывное образование.  В Caspian University студенты могут обучаться по государственным грантам и грантам ректора университета. Активисты и отличники учебы могут претендовать на стипендию от международных компаний и организаций. Все это позволяет дать студентам возможность развития в конкурентной среде современного общества.  ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (5, 'SATBAYEV UNIVERSITY', 'Almaty', 'Satbayev University – cтарейший и один из самых престижных технических университетов международного уровня в Казахстане, известный разработками в области горнорудного и нефтяного дела и ведущий широкую научную работу в тесной связи с производством. Университет уверенно занимает 1-ое место среди технических вузов в Казахстане по версии независимого агентства по обеспечению качества в образовании (IQAA-Ranking). И хотя в визитной карточке Satbayev University превалируют технические специальности, это многопрофильный университет, где можно изучать также архитектуру и менеджмент. В университете предусмотрены все уровни образования (бакалавриат, магистратура, докторантура, второе высшее дистанционное). Также Satbayev University занимает 73-е место в рейтинге университетов QS Emerging Europe And Central Asia University Rankings 2019/20. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (6, 'Сivil Aviation Academy', 'Almaty', 'Академия Гражданской Авиации является единственным высшим учебным заведением в Республике Казахстан и Центральной Азии осуществляющим подготовку высококвалифицированных инженеров и техников летного, технического и обслуживающего состава гражданской авиации, а также переподготовку и повышение квалификации специалистов предприятий ГА. Академия имеет новые учебные тренажеры «Tecnam» для визуальных полетов,  универсальный тренажер FNPT-II для тренировки инструментальных полетов, тренажеры самолетов Airbas-320, Boeing-737, для тренировки МСС-курса (взаимодействие многочленного экипажа). На аэродроме Боралдай открыт технический центр Академии по обслуживанию 10-ти учебных самолетов  Tecnam P2002 и Tecnam P92JS. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (8, 'ACADEMY OF LOGISTICS AND TRANSPORT', 'Almaty', 'Академия логистики и транспорта-это ведущее высшее техническое учебное заведение Казахстана по подготовке кадров для железнодорожного (включая городской электротранспорт и метрополитен) транспорта, коммуникации и логистической отрасли. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (16, 'ВОЕННО-ИНЖЕНЕРНЫЙ ИНСТИТУТ РАДИОЭЛЕКТРОНИКИ И СВЯЗИ', 'Almaty', 'Основная задача вуза – подготовка квалифицированных офицеров зенитных ракетных, радиотехнических войск, частей связи, радиотехнического обеспечения авиации, автоматизированных систем управления войсками и вооружением ВС РК. На сегодняшний день Военно-инженерный институт радиоэлектроники и связи является единственным высшим военным учебным заведением нашей страны, которое готовит не только офицеров, способных командовать воинскими коллективами, но и эксплуатировать радиоэлектронные средства, находящиеся на вооружении в соединениях и воинских частях ВС РК. Срок обучения в институте – 5 лет. По окончании учебного заведения выпускникам присваивается воинское звание «лейтенант». ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (18, 'ЕВРАЗИЙСКИЙ ТЕХНОЛОГИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Евразийский технологический университет – это ВУЗ нового поколения, сочетающий в себе элементы классического университета и новейших образовательных технологий.  Университет постоянно изучает потребности производств в специалистах и получения специализаций непосредственно на предприятиях. В этой связи нами подписано многостороннее соглашение (консорциум) с крупными производственными компаниями, среди которых АО «АгромашХолдинг», АО «АксайГазСервис», ГК «AllurAuto» и ГК «Автодом».  Евразийский Технологический Университет в рамках программы академической мобильности дает возможность своим студентам бесплатно пройти обучение зарубежном ВУЗе-партнере, оплатив при этом обучение только в ЕТУ. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (11, 'АЛМАТИНСКИЙ ГУМАНИТАРНО-ЭКОНОМИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Алматинский гуманитарно-экономический университет создан в сентябре 2021 года и является правопреемником Алматинской академии экономики и статистики. Учреждение «Алматинская академия экономики и статистики» преобразовано в Учреждение «Алматинский гуманитарно-экономический университет».  В структуре университета функционируют институты:  Университет уделяет особое внимание трудоустройству своих выпускников. Средний уровень трудоустройства выпускников АГЭУ по данным НПП «Атамекен» - 76%. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (22, 'КАЗАХСКАЯ НАЦИОНАЛЬНАЯ КОНСЕРВАТОРИЯ ИМ. КУРМАНГАЗЫ', 'Almaty', 'Казахская национальная консерватория имени Курмангазы – ведущий музыкальный вуз Республики Казахстан, обеспечивающий профессиональную подготовку специалистов в сфере музыкального образования. За годы работы коллективом консерватории был накоплен уникальный опыт подготовки кадров высочайшего уровня, востребованных не только в Казахстане, но и далеко за его пределами. Наш вуз выпустил множество выдающихся исполнителей и деятелей искусств, достойно представляющих наше государство, как в ближнем, так и дальнем зарубежье. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (15, 'АЛМАТИНСКИЙ ФИЛИАЛ НОУ ВПО "САНКТ-ПЕТЕРБУРГСКИЙ ГУМАНИТАРНЫЙ УНИВЕРСИТЕТ ПРОФСОЮЗОВ"', 'Almaty', 'Филиал СПбГУП в Алматы создан 29 февраля 1984 года первоначально в качестве учебно-консультационного пункта СПбГУП (в то время Высшей профсоюзной школы культуры) постановлением Секретариата ВЦСПС. В 1993 году преобразован в Алматинский филиал Санкт-Петербургского Гуманитарного университета профсоюзов.  Являясь крупнейшим филиалом знаменитого учебного заведения Российской Федерации, Алматинский филиал впитал в себя лучшие традиции более чем 90-летней истории головного вуза, российского высшего профессионального образования.  В Филиале готовят специалистов по направлениям подготовки: «Социально-культурная деятельность», «Психология», «Прикладная информатика», «Экономика», «Юриспруденция».  Обучение ведется с первого по четвертый курс. Завершение учебы и выдача дипломов по традиции происходит в Санкт-Петербургском Гуманитарном университете профсоюзов. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (31, 'КАЗАХСТАНСКО-НЕМЕЦКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Казахстанско-Немецкий Университет (DKU) - международный университет, который готовит востребованных специалистов со знанием двух иностранных языков, способных применять на практике преимущества немецкого образования для достижения карьерного и научного роста.  Вуз работает в рамках Соглашения между Правительством Республики Казахстан и Правительством Федеративной Республики Германия о дальнейшем сотрудничестве по развитию Казахстанско-Немецкого университета в городе Алматы, заключенного в Астане 3 сентября 2008 года и ратифицированного 15 июля 2010 года.  ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (26, 'КАЗАХСКИЙ НАЦИОНАЛЬНЫЙ МЕДИЦИНСКИЙ УНИВЕРСИТЕТ ИМ. С.Д.АСФЕНДИЯРОВА', 'Almaty', 'В КазНМУ им. С.Д.Асфендиярова работают известные ученые-педагоги Казахстана, академики Национальной академии наук РК, Российской академии медицинских наук, Академии профилактической медицины РК, Международных академий, заслуженные деятели науки и образования, заслуженные врачи и фармацевты. КазНМУ им. С.Д.Асфендиярова имеет договора сотрудничества с ВУЗами стран дальнего и ближнего зарубежья, среди которых: Нагасакский Университет (Нагасаки, Япония), Азиатско-Тихоокеанский университет (Беппо, Япония), Parkway College (Сингапур), Университет Перуджи (Перуджа, Италия), Задунайский Университет (Кремс, Австрия), Кыргызская Государственная медицинская Академия (Бишкек, Кыргызская Республика), Национальный медицинский университет им. А.Богомольца (Киев, Украина), Национальный фармацевтический университет (Харьков, Украина), Московская медицинская академия им. И.Сеченова (Москва, Россия) и др.  Полупроходные баллы на обучение по гранту в КазНМУ ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (35, 'МЕЖДУНАРОДНЫЙ ТРАНСПОРТНО-ГУМАНИТАРНЫЙ УНИВЕРСИТЕТ', 'Almaty', 'Университет был создан в 2000 году как некоммерческая, негосударственная организация в виде Учреждения «Казахский университет путей сообщения», готовит специалистов для транспортно-коммуникационного комплекса, строительства и других отраслей экономики РК.  Транспортная отрасль, являясь многопрофильным хозяйством, включает в себя сотни различных предприятий, испытывает потребность в специалистах различного профиля. Это организаторы движения, вагонники, локомотивщики, путейцы, связисты, автомобилисты, специалисты в области информационных систем и связи, энергетики, финансисты, бухгалтера и многие другие.  В 2023 году университет был переименован в «Международный транспортно-гуманитарный университет».  При Международный транспортно гуманитарный университете  действуют Технико-экономический колледж, что позволяет получить дефицитные специальности транспортно-коммуникационного комплекса, а в дальнейшем – высшее образование в университете.  В настоящее время Международный транспортно-гуманитарный университет ведет  подготовку кадров по 10-ми направлениям подготовки (25 образовательных программ) бакалавра, 8-ми направлениями подготовки (27 образовательных программ) магистратуры  и 2 направлениями подготовки докторантуры (3 образовательных программ).  Университет имеет партнерские отношения с такими высшими учебными заведениями, как Транспортный университет Чун Цин (КНР), Технический университет г.Варна (Болгария), Люблинский технический университет (Польша), Технический университет прикладных наук в г.Вилдау (Германия), Московский государственный технический университет им. Н.Э.Баумана, Санкт-Петербургский государственный университет телекоммуникаций им.М.А.Бонч-Бруевича, Московский государственный  университет  путей сообщения (МИИТ), Петербургский государственный  университет  путей сообщения (ПГУПС), Сибирская государственная автомобильно-дорожная  академия (СибАДИ), Ташкентский  институт  инженеров  железнодорожного транспорта (ТашИИТ), Кыргызский   государственный  университет  строительства,  транспорта и архитектуры (КГУСТА) и многие другие. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (37, 'УНИВЕРСИТЕТ  НАРХОЗ', 'Almaty', 'Университет Нархоз является одним из крупнейших международных научно-образовательных центров в Казахстане и Средней Азии. Ежегодно руководство встречается с зарубежными университетами – партнерами, представителями международных программ и организаций. Нархоз посещают крупнейшие исследователи, ученые и преподаватели. Университет участвует в международных исследовательских проектах, конкурсах и развивает международное партнерство с ведущими исследовательскими центрами. Нархоз сотрудничает с вузами-партнерами в рамках академической мобильности по программам Erasmus +, Ernst Mach, Abay-Vern, УШОС, государственная программа академической мобильности. Стратегическое партнерство с зарубежными вузами позволяет направлять студентов за счет собственных средств студентов. За последние 5 лет в программе академической мобильности приняли участие более 400 студентов и преподавателей. Университет является членом крупных международных ассоциаций ACPHA, РАБО, BMDA и активно сотрудничает с British Council, American Council, Bolashak. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (25, 'КАЗАХСКИЙ НАЦИОНАЛЬНЫЙ АГРАРНЫЙ ИССЛЕДОВАТЕЛЬСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'КазНАУ – один из ведущих вузов страны. За годы своей деятельности университет выпустил более 140 тыс. специалистов, внесших достойный вклад в развитие аграрного производства, образования и науки страны. Университет имеет мощную научно-образовательную инфраструктуру. Действует 7 научно-исследовательских институтов, 31 исследовательская лаборатория, инновационная теплица, 5 инновационных центров, в том числе Казахстанско-Японский и Казахстанско-Корейский инновационные центры. Являясь лидером аграрной науки и образования, университет создал крупную научную школу с целой плеядой выдающихся ученых. Качественную подготовку специалистов и научно-педагогических кадров обеспечивают 426 докторов и кандидатов наук, 16 академиков и членов-корреспондентов Национальной Академии наук РК. Доля преподавателей с учеными степенями и званиями составляет 65%.  ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (40, 'УНИВЕРСИТЕТ КУНАЕВА', 'Almaty', 'Университет Кунаева (до 2023 года - Евразийская юридическая академия имени Д.А.Кунаева) – единственное в Республике Казахстан частное специализированное учебное заведение с юридическим профилем. Университет имеет богатые традиции, многолетний опыт учебной и научной работы. За годы успешной деятельности подготовлено более 40 тыс. специалистов высшей квалификации.  Более 60% профессорско-преподавательского состава вуза -  известные в Казахстане ученые доктора и кандидаты наук. Это позволяет реализовать особую миссию академии в интеграции науки и образования, которая служит основой динамичного развития казахстанского общества. В целях усиления практической направленности в подготовке юридических кадров Университет Кунаева заключил меморандум о сотрудничестве в сфере подготовки специалистов с Министерством внутренних дел РК, Республиканской Коллегией адвокатов РК, Комитетом государственных доходов Министерства финансов РК, Прокуратурой города Алматы, Алматинским городским судом. Согласно достигнутой договоренностью с первыми руководителями правоохранительных служб Республики Казахстан вуз будет вести подготовку юридических кадров и для правоохранительных органов.  Академия располагает собственными учебно-административными корпусами, общежитиями, современными спорткомплексами. В 2012 году введен в эксплуатацию 12-этажный новый уникальный корпус, предоставляющий возможность обучающимся получать современные знания в различных направлениях высшего и послевузовского образования. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (1, 'МЕЖДУНАРОДНЫЙ ИНЖЕНЕРНО-ТЕХНОЛОГИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Международный инженерно-технологический университет (МИТУ, METU) - лицензированный и аккредитованный ВУЗ в верхней части города Алматы.  Полный цикл образования: колледж, бакалавриат, второе высшее, магистратура, докторантура.  Диплом METU признается в 48 странах мира и не требует подтверждения.  Помогаем на каждом этапе определиться с карьерными целями и реализовать мечту о карьере, бизнесе, науке, успешной и яркой жизни. Строим индивидуальный карьерный путь: 1 современная образовательная программа = 20 профессий.  Гранты: государственные от 50 баллов, ректорские, акиматовские, соцфондов.  Предлагаем трудоустройство по специальности с 1 курса.  Помогаем студентам выиграть гранты для участия в международных программах в 42 странах:  Предоставляем место в премиум-общежитие за небольшую стоимость каждому. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (39, 'УНИВЕРСИТЕТ КИМЭП', 'Almaty', 'Университет КИМЭП – ведущий независимый вуз Североамериканского образца в СНГ, пользующийся выдающейся репутацией в Казахстане и в регионе. Университет активно сотрудничает с деловым сообществом, а также с большим количеством партнеров – известных университетов Северной Америки, Европы и Азии. Содержание образования, предлагаемого Университетом КИМЭП, соответствует мировому классу, основано на международных стандартах и ведется полностью на английском языке. Обладая серьезной вспомогательной базой, включающей первоклассный Языковой Центр, крупнейшую в СНГ англоязычную библиотеку, современный cпортивный wентр и многое другое, Университет КИМЭП стремится предоставить бесценный академический опыт всем студентам и преподавателям. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (32, 'КАЗАХСТАНСКО-РОССИЙСКИЙ МЕДИЦИНСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Казахстанско-Российский медицинский университет – один из крупнейших частных вузов страны, четырежды прошедший государственную аттестацию и выдает диплом государственного образца.  Среди преподавателей университета – врачи с мировым именем, крупные казахстанские и зарубежные ученые. Обучение проводится как на договорной основе, так и по Государственному образовательному гранту. В КРМУ формируются небольшие академические группы по 6-10 человек, что позволяет организовать индивидуальный подход к студентам. Студенты имеют возможность свободы выбора языка обучения (государственный или русский) и с первого курса интенсивно изучают иностранные языки. Также у студентов есть возможность пройти обучение и практику за рубежом по программам академической мобильности.  В КРМУ действуют более 20 студенческих клубов и организаций, которые придают жизни университета яркость и уникальность. В их числе интеллектуальные клубы, спортивные команды, благотворительные организации и кружки, направленные на коллективное и индивидуальные творческое развитие. В стенах университета действует Комитет Деятельности Молодежи (КДМ), который занимается организацией студенческих мероприятий и курирует студенческую жизнь. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (12, 'АЛМАТИНСКИЙ ТЕХНОЛОГИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'АТУ является одним из ведущих университетов Казахстана и Центральной Азии по подготовке специалистов для пищевой, перерабатывающей, текстильной и легкой промышленности, инжиниринга и IT, экономики и бизнеса, сферы туризма, индустрии сервиса и гостеприимства.  Университет сотрудничает с 95 крупными учебно-образовательными учреждениями других стран. В университете можно пройти обучение и получить профессию на одном из 5 факультетов, функционирует 19 кафедр. Также представлены стипендиальные программы и программы двудипломного образования АТУ совместно с Российским Экономическим Университетом имени Плеханова, Итальянским Европейским Институтом Дизайна и Швейцарским Институтом Менеджмента, Туризма и Гостиниц. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (14, 'АЛМАТИНСКИЙ ФИЛИАЛ НАЦИОНАЛЬНОГО ИССЛЕДОВАТЕЛЬСКОГО ЯДЕРНОГО УНИВЕРСИТЕТА «МИФИ»', 'Almaty', '11 января 2022 года впервые об открытии филиалов зарубежных университетов заявил в своем послании президент Республики Казахстан Касым-Жомарт Токаев. 10 февраля 2022 года на встрече президентов В. Путина и К.-Ж. Токаева было принято решение открыть филиалы ведущих технических университетов Российской Федерации в Республике Казахстан, после чего было заключено соответствующее межправительственное соглашение. 1 июня 2022 года ректор КазНУ им. аль-Фараби Ж. Туймебаев и ректор НИЯУ МИФИ В. Шевченко подписали соглашение об открытии филиала. 12 сентября 2022 года в Алматы состоялось торжественное открытие первого учебного года в Алматинском филиале НИЯУ «МИФИ» на базе КазНУ им аль-Фараби.  Прием на 1-й курс Филиала на программы бакалавриата осуществляется на конкурсной основе по результатам вступительных испытаний, проводимых головной площадкой НИЯУ МИФИ, прохождение ЕНТ недостаточно.  Обучение по программам бакалавриата длится 4 года, из которых 3 года – в г. Алматы и 1 год – в г. Москва. Обучение по программам магистратуры длится 2 года с возможностью проходить практику в г. Москва. Согласно действующей лицензии не менее 50% дисциплин проводятся профессорско-преподавательским составом головной площадки и ее филиалов в Российской Федерации. Язык обучения – русский.  Обучение производится как по грантам Республики Казахстан, так и на платной основе. После успешного окончания обучения выдаются дипломы образца Российской Федерации. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (17, 'ВОЕННЫЙ ИНСТИТУТ СУХОПУТНЫХ ВОЙСК ИМ. САГАДАТА НУРМАГАМБЕТОВА', 'Almaty', 'Главная задача института состоит в подготовке высококвалифицированных офицеров сухопутных войск. Институт располагает современной учебно-материальной базой, разнообразными средствами технического обучения. Лекционные аудитории, учебные лаборатории, кабинеты и классы училища оборудованы всем необходимым для подготовки военных специалистов на современном уровне развития науки и техники. В учебном процессе широко применяются современные технические средства обучения, с использованием компьютерной и вычислительной аппаратуры. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (19, 'ЕГИПЕТСКИЙ УНИВЕРСИТЕТ ИСЛАМСКОЙ КУЛЬТУРЫ «НУР-МУБАРАК»', 'Almaty', 'Египетский университет исламской культуры «Нур-Мубарак» – единственный университет, подготавливающий высококвалифицированных исламоведов, религиоведов, филологов арабско-английского языка. Выпускникам вуза выдается диплом государственного образца. Занятия проводятся на арабском языке. Студенты вуза имеют возможность проходить курсы, практику в Египте и продолжить свое обучение, поступив в магистратуру и докторантуру. Со второго курса отличники учебы проходят практику в университетах Египта как аль-Азар, Александрия и Алмения. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (28, 'КАЗАХСКИЙ НАЦИОНАЛЬНЫЙ УНИВЕРСИТЕТ ИМ. АЛЬ-ФАРАБИ', 'Almaty', 'КазНУ имени аль-Фараби – ведущее учреждение системы высшего образования Республики Казахстан, первым прошедшее государственную аттестацию и подтвердившее право на осуществление образовательной деятельности по всем специальностям и уровням. КазНУ имени аль-Фараби – лидер Генерального рейтинга вузов Казахстана, первый в истории страны лауреат премии Президента Республики Казахстан «За достижения в области качества», дипломант Премии Содружества Независимых Государств за достижения в области качества продукции и услуг. На сегодняшний день КазНУ имени аль-Фараби это целый учебно-научный комплекс, который предлагает широкий выбор специальностей. В составе университета функционирует 16 факультетов, 67 кафедр, 32 научно-исследовательских институтов и центров, технопарк. В университете обучаются более 25 тысяч студентов и магистрантов по многоуровневой системе высшего профессионального образования. Университет сотрудничает с 418 крупнейшими международными вузами мира по реализации совместных международных программ обучения, обмену студентов и проведения стажировок. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (29, 'КАЗАХСКИЙ УНИВЕРСИТЕТ МЕЖДУНАРОДНЫХ ОТНОШЕНИЙ И МИРОВЫХ ЯЗЫКОВ ИМ. АБЫЛАЙ ХАНА', 'Almaty', 'Динамично развивающийся, инновационно-ориентированный Ablai Khan University занимает особую нишу в образовательном пространстве Республики Казахстан. Это специализированный университет, обобщающий лучшие казахстанские и международные образовательные традиции, и практики, умело сочетающий качество образовательного процесса с предметным разнообразием образовательных программ.  В настоящее время в университете имеется 5 базовых факультетов:  В университете действуют 13 ОП бакалавриата, 11 – магистратуры, 5 ОП –докторантуры. Образовательные программы университета прошли международную аккредитацию образовательных программ через систему Международных агентств ACQUIN и IAAR. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (36, 'МЕЖДУНАРОДНЫЙ УНИВЕРСИТЕТ ИНФОРМАЦИОННЫХ ТЕХНОЛОГИЙ', 'Almaty', 'Международный университет информационных технологий (МУИТ) является единственным вузом в Казахстане, получившим в 2019 г. институциональную международную аккредитацию ASIIN и прошедшим процедуру официального подтверждения соответствия международным стандартам. Согласно рейтингу Национальной палаты предпринимателей «Атамекен», МУИТ вошел в ТОП-3 вузов Казахстана, по таким образовательным программам, как «Компьютерные науки» и «Цифровая журналистика».  В настоящий момент в МУИТ обучаются более 6000 студентов. Университет активно формирует специалистов нового поколения в области информационно-коммуникационных технологий (ИКТ), а также в сферах электронного бизнеса, финансового инжиниринга и технологий, международной и цифровой журналистики. Особое внимание уделяется углубленному изучению английского языка, что расширяет возможности выпускников на мировом рынке труда и способствует успешной карьере в глобальной сфере ИКТ.  Существующая академическая мобильность позволяет студентам получать знания и международный опыт, обучаясь в течение семестра в вузах–партнерах, среди которых вузы и научно-образовательные организации США, Германии, России, Франции, Швейцарии, Кореи, Малайзии, Китая и др. Особую гордость у преподавателей Университета вызывает тот факт, что по окончании МУИТ процент трудоустроившихся выпускников всегда стабильно высок: порядка 90% от общего количества.  В МУИТ действует Студенческое Правительство, возглавляющее 15 студенческих организаций, среди них: благотворительная организация «Umit», танцевальный клуб «ShadowS», дебатный клуб «Debate Club», студенческое радио «ITTU FM», музыкальная организация «Auen», киберспортивная организация «Games IITU», клуб горного туризма «MountainKings» и т.д. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (41, 'УНИВЕРСИТЕТ МЕЖДУНАРОДНОГО БИЗНЕСА (UIB)', 'Almaty', 'Университет международного бизнеса имени Кенжегали Сагадиева – очевидный лидер в своем сегменте, занимающий почетное место среди 60 ведущих вузов в генеральном рейтинге РК. За период пребывания на рынке образования университетом разработаны программы, в которых сочетаются лучшие традиции академического обучения и прогрессивные технологии современного  бизнес-образования. Университет успешно применяет кредитную и дистанционную технологии обучения, что позволяет ему соответствовать международным образовательным стандартам. Благодаря внедрению инноваций в образовательной деятельности университет поддерживает широкие связи с крупнейшими высшими учебными заведениями России, США, Европы и Азии, осуществляя совместные проекты и внедряя программы по обмену студентами и преподавателями. В UIB за 4 года можно получить три диплома: государственный казахстанский, польский и американский. Университет имеет и государственную, и международную аккредитацию. Это значит, что диплом будет признан во всем мире. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (10, 'АЛМАТИНСКАЯ АКАДЕМИЯ МВД РК ИМ. МАКАНА ЕСБУЛАТОВА', 'Almaty', 'Алматинская академия Министерства внутренних дел Республики Казахстан является ведущим республиканским государственным учреждением, обеспечивающим подготовку кадров для органов внутренних дел по программам высшего, послевузовского и дополнительного образования. В Академии созданы все условия для полноценной подготовки специалистов для органов внутренних дел: 12 лекционных залов, 12 компьютерных классов, 4 лаборатории, 2 полигона, 80 учебных аудиторий, 15 учебно-методических кабинетов кафедр, кабинет педагогического мастерства, 4 библиотеки, 7 читальных залов, с общим фондом свыше 350 тысяч единиц изданий литературы, учебно-полевой лагерь, стадион, 3 спортивных комплекса, учебный комплекс огневой подготовки и тир. Академия располагает современной материально-технической и учебно-методической базой, что позволяет слушателям эффективно овладевать необходимыми теоретическими знаниями и практическими навыками. В учебном процессе широко используются учебно-методические кабинеты кафедр. Преподаватели, курсанты, магистранты и докторанты имеют свободный доступ к информационно-справочным системам и ресурсам сети Интернет. Все кабинеты и лаборатории оборудованы современными техническими средствами, стендами, наглядными пособиями. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (7, 'АКАДЕМИЯ КОМИТЕТА НАЦИОНАЛЬНОЙ БЕЗОПАСНОСТИ РК', 'Almaty', 'История Академии КНБ началась в 1974 году, когда в г. Алма-Ате были созданы постоянно действующие Курсы усовершенствования руководящего и оперативного составов органов государственной безопасности СССР. Спустя 5 лет учебное заведение преобразовано в Курсы повышения квалификации кадров КГБ СССР, а в 1983 году им придан статус Высших курсов КГБ СССР. 26 января 1994 года учебное заведение было реорганизовано в институт Комитета нацио­нальной безопасности. Указом Главы государства от 6 мая 1999 года Институт преобразован в Академию КНБ РК, которая на сегодняшний день является специальным многопрофильным ведущим учебным заведением, научным центром в сфере подготовки, переподготовки и повышения квалификации руководящих и оперативных работников органов национальной безопасности.  Деятельность академии отличает инновационный характер – многоступенчатая модель вуза с современной инфраструктурой и передовыми технологиями подготовки. В настоящее время Академия КНБ проводит набор по следующим образовательным программам: 1) по программе высшего образования (срок обучения – 4 года), специальность – контрразведывательная деятельность; 2) по программе дополнительного образования, курсы переподготовки (срок обучения – не менее 6 месяцев). Также в учебном заведении действует обучение по программам магистратуры и докторантуры для действующих сотрудников Комитета.  Образовательный процесс Академии предусматривает тесное взаимодействие с подразделениями КНБ, что позволяет повысить его результативность.  ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (23, 'КАЗАХСКИЙ АВТОМОБИЛЬНО-ДОРОЖНЫЙ ИНСТИТУТ ИМ. Л.Б. ГОНЧАРОВА', 'Almaty', 'Идея создания автомобильно-дорожного образовательного учреждения пришла будущему (и действующему ныне) ректору вуза Кабашеву Рахимжану Абылкасымовичу в конце далеких 1990-х гг. Проработав более 30-ти лет в системе образования и в ведущих вузах страны, став свидетелем перехода советской системы образования в казахстанскую, а также свидетелем объединения Алматинского автодорожного института (ААДИ) и Алматинского института инженеров железнодорожного транспорта (АЛИИТ) в Казахскую Академию транспорта и коммуникаций, ученому стало понятно, что любимые специальности могут обрести совсем другую направленность и потерять былую точность и системность. Так в 1999 году был создан Казахский автомобильно-дорожный институт (КазАДИ) (государственная лицензия АБ№ 0142694). Пройдя тернистый путь от института до академии и обратно, сегодня КазАДИ является единственным специализированным техническим вузом в республике, готовящим кадры всего образовательного звена, начиная с бакалавра и заканчивая PhD, для автомобильно-дорожной отрасли родной Республики Казахстан. Выбрать КазАДИ - значит стать обладателем редкой специальности посредством лучших современных программ обучения, созданных на основе многолетнего академического и практического опыта. Отличительной особенностью программ вуза является тесное сотрудничество с производственными предприятиями города Алматы, на которых существуют кафедры и лаборатории, позволяющие студентам получать настоящий практический курс. Вуз прошел институциональную аккредитацию сроком на 5 лет, аккредитацию прошли и все образовательные программы. В институте функционируют языковые курсы для иностранных граждан, желающих освоить казахский, английский или русский языки для поступления в институт. На языковых курсах успешно отучились граждане Израиля, Бангладеш, Индии, Сомали. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (34, 'МЕЖДУНАРОДНАЯ ОБРАЗОВАТЕЛЬНАЯ КОРПОРАЦИЯ', 'Almaty', 'Правопреемник Казахской головной архитектурно-строительной академии (КазГАСА) и Казахско-Американского университета (КАУ). Имеет высококвалифицированный преподавательский корпус; большинство преподавателей - профессора, признанные исследователи и специалисты, авторы многих публикаций и монографий. Ежегодно МОК выпускает высококвалифицированных специалистов в сферах архитектуры, дизайна, строительства, экономики и менеджмента; профессиональных, полиязычных специалистов в области связи и телекоммуникаций.  Сегодня МОК: - многопрофильный вуз, который реализует подготовку кадров по 10 направлениям подготовки бакалавриата (32-м образовательным программам), 4 направлениям подготовки магистратуры (9 образовательным программам) и 3 направлениям подготовки докторантуры (6 образовательным программам); - крупный специализированный вуз в области архитектуры и строительства, координирующий работу факультетов и кафедр в 41 вузе Казахстана; - первый в мире вуз, прошедший аккредитацию в комиссии ЮНЕСКО-МСА; - первый в Казахстане вуз, внедривший кредитную систему обучения; - имеет соглашения о сотрудничестве со 100 ведущими зарубежными вузами.  В университете ведется непрерывная работа по расширению международных связей, заключены договора о сотрудничестве в области образования и науки с зарубежными вузами Италии, Великобритании, Германии, Бельгии, Израиля, Японии, США, Королевства Нидерландов, ОАЭ, Южной Кореи, Китая, Венгрии, Чехии, Румынии, Польши, Болгарии, Турции и стран СНГ.  Университет имеет в наличии современную материально-техническую базу, которая включает в себя: главный учебный корпус; административно-учебный корпус, главный административный корпус; два спортивных зала; многофункциональную спортивную площадку, теннисный корт; общежитие - Дом студентов. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (38, 'УНИВЕРСИТЕТ «ТУРАН»', 'Almaty', 'Университет «Туран» (Turan university) осуществляет подготовку по 29 образовательным программмам бакалавриата, 27 специальностям магистратуры, 6 - докторантуры PhD по самым востребованным направлениям на трех факультетах: экономическом, гуманитарно-юридическом, факультете «Цифровые технологии и искусство» и Высшей школе бизнеса.  Для абитуриентов университета действует Turan Foundation – подготовительный факультет, для желающих уверенно выпуститься из школы и поступить в вуз. Это выгодная инвестиция в будущее и хорошая платформа  для успешной учебы в вузе.  В университете «Туран» студенты могут обучаться по государственным грантам и грантам ректора университета. Отличники учебы могут претендовать на стипендию казахстанских и зарубежных компаний и организаций. Все это позволяет дать студентам качественное образование для их дальнейшего трудоустройства и развития в конкурентной среде современного общества.  IT-инфраструктура университета насчитывает более 600 персональных компьютеров. Внутренняя система поддерживается мощным сервером. Университет «Туран» стал первым вузом в Республике Казахстан, который присоединился к проекту Edurоam, что позволяет студентам и сотрудникам университета пользоваться бесплатным Wi-Fi практически в любом вузе 54 стран мира под своим корпоративным логином.  В университете «Туран» много мест, специально предназначенных для отдыха и досуга студентов. Для всех желающих открыты две кофейни с уютными диванчиками и столиками, две столовые с вкусной едой и специальная «chill out» зона с напольными шахматами и настольным теннисом. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (13, 'АЛМАТИНСКИЙ УНИВЕРСИТЕТ ЭНЕРГЕТИКИ И СВЯЗИ ИМ. Г.ДАУКЕЕВА', 'Almaty', 'Алматинский университет энергетики и связи имени Гумарбека Даукеева — один из ведущих технических вузов Центральной Азии, который осуществляет подготовку кадров в сфере энергетики, телекоммуникаций, IT-технологий и информационной безопасности, космической инженерии, робототехники и искусственного интеллекта. Университет готовит специалистов по программам колледжа, бакалавриата, магистратуры и докторантуры PhD. Согласно международному рейтингу U-Multirank за 2019 год, АУЭС имени Г. Даукеева вошел в Тройку лучших университетов Казахстана. Является первым негосударственным техническим вузом со статусом некоммерческой организации. Обучение ведется на казахском, русском и английском языках.  Выпускники АУЭС им. Г.Даукеева активно участвуют в программе Болашак, обучаются и получают степени магистра, доктора наук PhD, как в вузах стран СНГ, так и в ведущих зарубежных вузах. Также выпускники успешно трудоустраиваются по специальности не только в Казахстане, но и в странах ближнего и дальнего зарубежья: Россия, Германия и другие страны Европейского Союза, США, Великобритания, Канада, Израиль и т.п. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (27, 'КАЗАХСКИЙ НАЦИОНАЛЬНЫЙ ПЕДАГОГИЧЕСКИЙ УНИВЕРСИТЕТ ИМ. АБАЯ', 'Almaty', 'Сегодня КазНПУ имени Абая - крупный научно-образовательный центр в области высшего и послевузовского образования по направлениям подготовки кадров: «Педагогические науки», «Искусство и гуманитарные науки», «Естественные науки», «Социальные науки», «Бизнес, управление и право» и т.д. В составе университета функционирует семь институтов: Педагогики и психологии; Математики, физики и информатики; Филологии; Искусств, культуры и спорта; Естествознания и географии; Истории и права; «Сорбонна-Казахстан». Важным событием в жизни университета стало открытие Института Сорбонна-Казахстан - филиала университета Сорбонна-Париж, который имеет более 800-летнюю историю. В университете работает 34 кафедры, в том числе три общеуниверситетских (Военная кафедра, педагогики ЮНЕСКО, кафедра Ассамблеи народа Казахстана). В университете действуют 15 диссертационных советов, Совет молодых ученых, Научная библиотека и издательство «Ұлағат». Недавно были созданы Центр педагогического мастерства, Цифровой центр обслуживания студентов «Шапагат», Международная научная лаборатория технологий информатизации и образования. В целом, образовательная деятельность университета огромна, постоянно ставятся и решаются новые задачи. Вместе с тем в КазНПУ имени Абая не забывают и историю его развития. Так, установлены мемориальные доски, посвященные основателям вуза - Ахмету Байтурсынову, Санжару Асфендиярову, Оразу Жандосову, Сакену Сейфуллину, Баймену Альманову, Халелу Досмухамедову, Кудайбергену Жубанову, Ильясу Кабылову, Алимхану Ермекову. Эти имена великих сынов казахского народа будут служить примером для будущих поколений казахстанцев. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (30, 'КАЗАХСТАНСКО-БРИТАНСКИЙ ТЕХНИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Казахстанско-Британский технический университет является лучшим техническим вузом Республики на протяжении последних лет согласно рейтингу Независимого агентства по обеспечению качества в образовании. 98% выпускников КБТУ находят работу вскоре после окончания университета или продолжают обучение. Некоторые из них приглашаются компаниями еще во время учебы. Многие наши выпускники работают в ведущих международных компаниях IT и нефтегазовой отраслей, в том числе, в Силиконовой долине. У университета налажены партнерские отношения и сотрудничество с лучшими мировыми вузами, такими как University of London, Harvard Business School и многие другие. Также КБТУ является участником программ обмена преподавателями и студентами, в том числе Erasmus+ и Болашак. Университет увеличивает количество мест для иностранных учащихся и ученых. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (33, 'КАЗАХСТАНСКО-ШВЕЙЦАРСКИЙ ИНСТИТУТ ТУРИЗМА И ГОСТИНИЧНОГО БИЗНЕСА ПРИ АТУ', 'Almaty', 'C 2014 года реализуется совместная казахстанско-швейцарская программа — двудипломное образование. Алматинского Технологического университета и Международного института менеджмента туризма и гостиниц — HTMi (Швейцария) по подготовке кадров для отраслей: туризм, ресторанное дело и гостиничный бизнес, а также организация мероприятий.  Целью программы является подготовка профессионалов, обладающих современными знаниями и практическими навыками в сфере туризма, управления ресторанным и гостиничным бизнесом, конкурентоспособных на казахстанском и зарубежном рынках труда.  При успешном освоении выпускнику программы вручаются: диплом Международного института менеджмента туризма и гостиниц — HTMi, Швейцария и диплом АТУ государственного образца РК о присуждении академической степени бакалавра. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (21, 'КАЗАХСКАЯ НАЦИОНАЛЬНАЯ АКАДЕМИЯ ИСКУССТВ ИМ. Т.К.ЖУРГЕНОВА', 'Almaty', 'Казахская национальная академия искусств имени Т.К.Жургенова – многоступенчатое многопрофильное учебное заведение, признанный центр подготовки творческой интеллигенции, выпускники которого составляют золотой фонд мировой и национальной культуры и искусства. КазНАИ им. Т.К.Жургенова является эталоном высоких стандартов художественного образования в сфере искусства и культуры, крупнейшим учебно-научным центром подготовки высококвалифицированных специалистов и научных кадров. В академии функционируют 6 факультетов: Подготовку специалистов осуществляют 23 кафедры, из которых 17 являются выпускающими, 6 – общеакадемическими. Функционирует экспериментальный молодежный театр «Мен» (руководитель — заслуженный деятель искусств РК, доцент Н.Жакыпбай), лауреат Международного и Республиканского фестивалей, в репертуар которого входят пьесы национальной и мировой драматургии — «Гамлет», «Пигмалион», «Ревизор», «Король Лир», «Үшкір мүйізді бұзау», «Сәукелелі ақ тау», «Аққұс туралы аңыз», «Қош бол, ақ кеме» и др. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (24, 'КАЗАХСКИЙ ГОСУДАРСТВЕННЫЙ ЖЕНСКИЙ ПЕДАГОГИЧЕСКИЙ УНИВЕРСИТЕТ', 'Almaty', 'Международное сотрудничество является неотъемлемой частью деятельности Казахского государственного женского педагогического университета и важным инструментом в обеспечении качества образования и его соответствия международным стандартам. Эта работа осуществляется в рамках программ сотрудничества с ведущими зарубежными университетами. На сегодняшний день университет подписал более 50 соглашений о сотрудничестве с вузами России, Кыргызстана, Узбекистана, Азербайджана, Турции, Польши, Франции, Китая, Болгарии, Малайзии, Южной Кореи, Монголии, США, Литвы, Испании, Великобритании. Благодаря программе академической мобильности  у обучающихся КазНацЖенПУ есть уникальная возможность выехать на обучение в вузы-партнеры США, Турции, Польши,Франция, Великобритании, Китая и России. На сегодняшний день КазНацЖенПУ является активным участником международных программ академической мобильности, таких как Erasmus+, Mevlana. ', NULL, NULL);
INSERT INTO universities (id, name, address, description, email, pic_name) VALUES (20, 'КАЗАХСКАЯ АКАДЕМИЯ СПОРТА И ТУРИЗМА', 'Almaty', 'Казахская академия спорта и туризма – ведущий вуз Казахстана по подготовке специалистов в области физической культуры и спорта, туризма, сервиса, ресторанного дела и гостиничного бизнеса, главная кузница тренерских кадров. Сегодня академия имеет все условия для качественной подготовки специалистов и профессионального роста педагогического коллектива. Студенты занимаются в уютных аудиториях, тренируются в современных спортивных залах. При Академии действует Республиканское учебно-методическое объединение по специальностям «Физическая культура и спорт» и «Туризм». Академия является разработчиком Государственной программы развития физической культуры и спорта, государственных образовательных стандартов высшего профессионального образования по специальностям вуза. ', NULL, NULL);